function activarBarrio(nombre) {
    barrios[nombre].poligono.setMap(map);
    if (nombre === "BALVANERA")
        barrios[nombre].infowindow.content = generarHTMLTabla(nombre,"/ONCE");
    else
        barrios[nombre].infowindow.content = generarHTMLTabla(nombre,"");
    if (barrios[nombre].listenerCreado === false) {
        barrios[nombre].listenerCreado = true;
        google.maps.event.addListener(barrios[nombre].poligono, 'click', function (event) {
            //prompt(nombre,event.latLng.lat()+ ', ' + event.latLng.lng());
            barrios[nombre].infowindow.open(map, barrios[nombre].marker);
            buscarBarrio(nombre);
        });
    }
}
function desactivarBarrio(nombre) {
    barrios[nombre].poligono.setMap(undefined);
}


var contadorProductos = [];
    function setearCeroContadorProductos(){

        for (var barrio in barrios) {
  
            contadorProductos[barrios[barrio].nombre] = 0;
            desactivarBarrio(barrios[barrio].nombre);
        }
    };
    function activarTodosLosBarrios() {
        for (var barrio in barrios) {
            activarBarrio(barrios[barrio].nombre);
        }
         limpiarDivs(divs);
        getDiv("inicio").style.visibility = "visible";
    }

function generarHTMLTabla(barrio,nombreInventado) {
    return '<div id="content">' +
        '<div id="siteNotice">' +
        '</div>' +
        '<h4 id="firstHeading" class="firstHeading">' + barrio+nombreInventado + '</h4>' +
        '<div id="bodyContent">' +
        '</div>' +
         '<table><thead><tr><th>Foto</th><th>Descripción</th><th>Precio</th></tr></thead><tbody id ="' + barrio + '">' +
	'</tbody>' +
'</table>' +
'</div>';

}

